mapper.select:
SELECT hi.apt_seq , hi.apt_nm , CONCAT(dc.sido_name, ' ', dc.gugun_name, ' ', dc.dong_name, ' ', hi.jibun) AS address , hd.deal_amount price , hi.latitude , hi.longitude , (SELECT ROUND(AVG(CAST(REPLACE(deal_amount, ',', '') AS UNSIGNED))) FROM housedeals WHERE apt_seq = hi.apt_seq ) AS avg_price_all ,(SELECT ROUND(AVG(CAST(REPLACE(deal_amount, ',', '') AS UNSIGNED))) FROM housedeals WHERE apt_seq = hi.apt_seq AND STR_TO_DATE(CONCAT(deal_year, LPAD(deal_month, 2, '0'), LPAD(deal_day, 2, '0')), '%Y%m%d') BETWEEN STR_TO_DATE(CONCAT(hd.deal_year, LPAD(hd.deal_month, 2, '0'), LPAD(hd.deal_day, 2, '0')), '%Y%m%d') - INTERVAL 1 MONTH AND STR_TO_DATE(CONCAT(hd.deal_year, LPAD(hd.deal_month, 2, '0'), LPAD(hd.deal_day, 2, '0')), '%Y%m%d') + INTERVAL 1 MONTH ) AS avg_price_near_peak FROM houseinfos hi JOIN dongcodes dc ON CONCAT(hi.sgg_cd, hi.umd_cd) = dc.dong_code JOIN housedeals hd ON hd.no = ( SELECT no FROM housedeals WHERE apt_seq = hi.apt_seq ORDER BY CAST(REPLACE(deal_amount, ',', '') AS UNSIGNED) DESC LIMIT 1 ) WHERE hi.apt_nm like concat('%', #{keyword}, '%')
AND ( SUBSTRING_INDEX(hi.apt_seq, '-', 1) + 0 > SUBSTRING_INDEX(#{lastAptSeq}, '-', 1) + 0 OR ( SUBSTRING_INDEX(hi.apt_seq, '-', 1) + 0 = SUBSTRING_INDEX(#{lastAptSeq}, '-', 1) + 0 AND SUBSTRING_INDEX(hi.apt_seq, '-', -1) + 0 > SUBSTRING_INDEX(#{lastAptSeq}, '-', -1) + 0 ) )
ORDER BY SUBSTRING_INDEX(hi.apt_seq, '-', 1) + 0 , SUBSTRING_INDEX(hi.apt_seq, '-', -1) + 0 LIMIT #{size}
